import * as React from 'react';
import { Avatar, Container, Content, Details, Footer, Header, InputWrapper, Label, Name, Wrapper  } from '../../styles';

interface IProps  {
  type: string;
  user: any;
}

interface IState {
  fromEmail: string;
  toEmails: string;
  subject: string;
  textPlain: string;
};

class Base extends React.Component<IProps, IState> {
  constructor(props) {
    super(props);

    this.state = {
      fromEmail: 'bfyhdgzj@gmail.com',
      toEmails: 'munkhorgil@live.com',
      subject: 'Hello World',
      textPlain: 'Hello Worlaksjdlkajs',
    };
  }

  onChange = <T extends keyof IState>(key: T, event: any) => {
    this.setState(({ [key]: event.target.value }) as Pick<IState, keyof IState>);
  };

  onSend = () => {
    console.log(this.state);
  };

  renderInput(label: string, value: string, name: string) {
    return (
      <InputWrapper>
        <Label>{label}</Label>
        <input type="text" value={value} onChange={this.onChange.bind(this, name)}/>
      </InputWrapper>
    );
  }

  renderFrom(label: string, email: string) {
    return (
      <InputWrapper>
        <Label>{label}: {email}</Label>
      </InputWrapper>
    );
  }

  renderDetails() {
    const { fromEmail, toEmails, } = this.state;
    const { type, user } = this.props;

    return (
      <Details>
        <Name>{user.firstName}</Name>
        {type === 'new' ? (
          <>
            {this.renderFrom("From", fromEmail)}
            {this.renderInput("To", toEmails, 'toEmails')}
          </>
        ) : null}
      </Details>
    );
  }

  renderHeader() {
    const { user } = this.props;

    return (
      <Header>
        <Avatar>{user.firstName}</Avatar>
        {this.renderDetails()}
      </Header>
    );
  }

  renderBody() {
    const { textPlain } = this.state;
    const { type } = this.props;

    return (
      <Container>
        {type === 'new' ? (
          <Content>
            <textarea value={textPlain} onChange={this.onChange.bind(this, 'textPlain')} />
          </Content>
        ) : null}
      </Container>
    );
  }

  renderFooter() {
    return (
      <Footer>
        <button onClick={this.onSend}>Send</button>
      </Footer>
    );
  }

  render() {
    return (
      <Wrapper>
        {this.renderHeader()}
        {this.renderBody()}
        {this.renderFooter()}
      </Wrapper>
    );
  }
}

export default Base;
